mapper.select:
SELECT tag
FROM hashtag
WHERE memberId = #{id}

SELECT tag
FROM hashtag
WHERE relId = #{relId}

SELECT tag
FROM hashtag
WHERE relId = #{relId}

SELECT *
FROM hashtag
WHERE relId = #{relId}
AND relTypeCode = #{relTypeCode}

SELECT id
FROM
hashtag
WHERE tagNo = #{tagNo}
AND relId = #{relId}

mapper.update:
UPDATE hashtag
SET updateDate = NOW(),
tag = #{tag}
WHERE relId = #{relId}
AND tagNo = #{tagNo}

mapper.delete:
DELETE FROM hashtag
WHERE relId = #{relId}
AND tag =
#{tag}
AND memberId = #{memberId}

DELETE FROM hashtag
WHERE relId = #{relId}
AND memberId
= #{memberId}
